        public void Save()
        {
            currentGame.playerHasSavedOnce = true;
            string json = JsonConvert.SerializeObject(currentGame);
            File.WriteAllText(Environment.CurrentDirectory + "\\SaveFile.txt", json);
            WriteLineText("Progress was recorded.");
            Thread.Sleep(800);

            
        }

        public void LoadSaveData()
        {
            try
            {
                StreamReader s = new StreamReader(Environment.CurrentDirectory + "\\SaveFile.txt");
                string json = s.ReadToEnd();
                var deserialisedData = JsonConvert.DeserializeObject<PlayGame>(json);
                s.Close();
                currentGame = deserialisedData; //Update all variables
                currentGame.MainMenu = this;
                currentGame.Game();
            }
            catch (Exception)
            {
                Console.WriteLine("No data to load");
                
            }
           
        }
    }
